INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (7, 'Mariusz Szczygieł', 'Nowa książka Mariusza Szczygła jest esejem napisanym z miłości do reportażu. To lektura dla wszystkich, którzy kochają ten gatunek oraz dla tych, którzy mają wątpliwości, czy jest wiarygodny, a także poradnik dla tych, którzy sami chcą pisać.', 'wY5AzwEACAAJ', 400, 'https://s.lubimyczytac.pl/upload/books/5013000/5013390/978467-352x500.jpg', 7.20, 'Fakty muszą zatańczyć', 2022);
