INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (14, 'Marly Michelle', '<p><b>Artystka, primadonna, bogini</b></p><p><b>Kobieta, której powołaniem była sztuka i miłość </b></p><p>Wenecja, 1957: Maria Callas jest największą śpiewaczką operową swoich czasów, ale artystyczna doskonałość, którą uosabia na scenie, zaczyna zbierać żniwo. Wspaniały głos odmawia jej posłuszeństwa, a przemęczona artystka marzy o chwili wytchnienia – na co jednak nie pozwala jej ani świat opery, ani jej mąż i menadżer Meneghini. Jej życie całkowicie podporządkowane jest karierze, nie ma w nim miejsca dla niej jako kobiety. </p><p>Na jednym z przyjęć Maria spotyka armatora Arystotelesa Onassisa – przystojnego greckiego milionera. Wbrew wszelkim przeciwnościom zakochują się w sobie. W latach sześćdziesiątych uznawani są za parę idealną. Do czasu, aż Onassis nie pozna kolejnej ikony tamtych czasów – Jackie Kennedy...</p><p>Magiczna i piękna powieść o Marii Callas, uosobieniu elegancji i charyzmy, wybitnej artystce, ale przede wszystkim – namiętnie kochającej kobiecie.</p><p> </p><p><b> </b></p><p><b>Powyższy opis pochodzi od wydawcy.</b></p>', 'X3txEAAAQBAJ', 382, 'http://books.google.com/books/publisher/content?id=X3txEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE718nYGvibVQcRCCUCS9qJ2lslYtjV3GBslph__ofUSPk87BMmtMYCpSdlVtyh5cwtXpZYz5MOUaiWbtWseMLEXGfTUVwhn83amIofOS8wk8q5EeY-XaHzDI2EFcvpjP3qfOuJKI&source=gbs_api', 0.00, 'Maria Callas i głos serca', 2022);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (15, 'Marian Eckert', null, 'IfRoAAAAMAAJ', 349, 'http://books.google.com/books/content?id=IfRoAAAAMAAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE700fL-G2fQPGA9uKL670kQU26h8tbWmL3VCCXVLgLeVelWF2Xv972Wn9dJtXTErgpczT19GScqC97WDlu1muxMsEoX1oOMXn79wzV3ZhnZIUNDA78qFOpwdxmFpmCpHxsme63gG&source=gbs_api', 0.00, 'Historia Polski, 1914-1939', 1990);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (16, 'Witold Gombrowicz', 'In this bitterly funny novel by the renowned Polish author Witold Gombrowicz, a writer finds himself tossed into a chaotic world of schoolboys by a diabolical professor who wishes to reduce him to childishness. Originally published in Poland in 1937, <i>Ferdydurke </i>became an instant literary sensation and catapulted the young author to fame. Deemed scandalous and subversive by Nazis, Stalinists, and the Polish Communist regime in turn, the novel (as well as all of Gombrowicz''s other works) was officially banned in Poland for decades. It has nonetheless remained one of the most influential works of twentieth-century European literature.<br><i>Ferdydurke </i>is<i> </i>translated here directly from the Polish for the first time. Danuta Borchardt deftly captures Gombrowicz''s playful and idiosyncratic style, and she allows English speakers to experience fully the masterpiece of a writer whom Milan Kundera describes as “one of the great novelists of our century.”<br><br>', 'flECgcJ4r3MC', 320, 'http://books.google.com/books/content?id=flECgcJ4r3MC&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE70xWWR7abRCYzDUhSldY2tfKf3gcjkoOKKgW85AXUHlX2_EGkHNn_5w-34hPZmzqqWJN4YaCcW8eTHKV40f4eXPi-sltGqVc6xHuJpWh58rMDjv_eSESdu2v08uq1czd30PNLWY&source=gbs_api', 4.00, 'Ferdydurke', 2012);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (17, 'Adam Mickiewicz', null, '0TRt3C4eYusC', 378, 'http://books.google.com/books/content?id=0TRt3C4eYusC&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE73x3pBKxnybKA_zYkect4lGSjKxJr36n-ac3a78mOwWHvv2fKJLvN-Z8BDJHobPl3JAXkTlDukT7eN3J75SRdmCXizQJo5hg2t3vQWiPz9o5J8JdQEg88unbP21wX6-ENwj0Iqh&source=gbs_api', 0.00, 'Pan Tadeusz czyli ostatni zajazd na Litwie', 1878);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (18, 'Randall Munroe', '<p><b>WHAT IF... one man decided to answer all the unanswerable questions, using science.</b><br><b><br>The <i>Sunday Times</i>-bestselling author and <i>xkcd</i> creator, Randall Munroe</b> <b>is here to provide the best answers yet to the important questions you probably never thought to ask.</b><br><br>The millions of people around the world who read and loved <i>What If?</i> still have questions, and those questions are getting stranger.<br><br>Planning to ride a fire pole from the moon back to Earth? The hardest part is sticking the landing.<br><br>Hoping to cool the atmosphere by opening everyone''s freezer doors at the same time? Maybe it''s time for a brief introduction to thermodynamics.<br><br>Want to know what would happen if you rode a helicopter blade, built a billion-storey building, made a lava lamp out of lava, or jumped on a geyser as it erupted? Okay, if you insist.<br><br>Welcome (back) to the mind-blowing world of <i>What If</i>?<br><br>Unfazed by absurdity, Randall consults the latest research on everything from swing-set physics to airplane-catapult design to clearly and concisely answer his readers'' questions. As he consistently demonstrates, you can learn a lot from examining how the world might work in very specific extreme circumstances.<br><br>Filled with bonkers science, boundless curiosity, and Randall''s signature stick-figure comics, <i>What If? 2</i> is sure to be another instant classic adored by inquisitive readers of all ages.</p>', 'UZJbEAAAQBAJ', 368, 'http://books.google.com/books/publisher/content?id=UZJbEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE72W3RI8VfmTEIQWi70RLaSKe7jv5KB1Mtmgllwr80zXGA3fvBalYeOUwuJcTQkXQqutN-3tmsEMGLJ7N6dDlTBtl7mVGsQSxslQxiwA32GuxdXhqdmkvsxuzEu7D9S0gBmD74o8&source=gbs_api', 4.50, 'What If?2', 2022);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (19, 'Fiodor Dostojewski', '23-letni Rodion Raskolnikow kończy studia prawnicze, ale ciężka sytuacja finansowa jego rodziny utrudnia mu rozpoczęcie praktyki w zawodzie. Młody mężczyzna jest zwolennikiem filozofii, zgodnie z którą jednostka wybitna ma prawo dokonywać zbrodni na zwykłych ludziach. Raskolnikow publikuje na ten temat manifest w prasie. Nie chcąc dopuścić, by jego siostra wyszła za mąż wyłącznie dla pieniędzy, decyduje się zabić dla zarobku starą lichwiarkę, którą traktuje jako społeczną "wszę". Sytuacja komplikuje się, gdy na miejscu niespodziewanie pojawia się jej siostra Lizawieta. Po dokonaniu podwójnego zabójstwa wciąż przekonany do swoich racji Raskolnikow przechodzi załamanie nerwowe. Przygniatające przeczucie nieuniknionej kary sprowadza na niego gorączkę i majaki. Fiodor Dostojewski to jeden z najsłynniejszych klasyków literatury rosyjskiej, przeplatający w swoich utworach realizm i wyobraźnię. Mistrz prozy psychologicznej, w której jasno wyrażał swoją niechęć do socjalizmu i rewolucji. Uważał, że ma to niszczący wpływ na człowieka, pozbawia go wartości chrześcijańskich, które dla Dostojewskiego były niezwykle ważne.', '6AdPEAAAQBAJ', 343, 'http://books.google.com/books/publisher/content?id=6AdPEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE70GQnNtCG1fmHJN2dcnFaTCg21tppwvmJh9gzoWxNjZb_tErS4m5MLMMNO94zJh3csjotBF0cMqFV3xuguaAKXrksoH9R3JFbvm89qykOqYNgtOuqhYUlOtwL-tfXLVxszyM8fC&source=gbs_api', 0.00, 'Zbrodnia i Kara', 2021);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (20, 'Lew Tołstoj', 'Klasyka rosyjskiej literatury, uważana za rosyjską epopeję narodową. To wielowątkowa opowieść o losach obywateli carskiej Rosji w burzliwych czasach wojen napoleońskich. Tołstoj przedstawił realistyczną panoramę polityczno-społecznych układów w cesarstwie, wnikliwie budując psychologię postaci, przedstawiając ówczesne obyczaje i kulturę. Nic dziwnego, że misternie zbudowany utwór pełen historycznych wydarzeń, działań wojennych, wystawnych przyjęć, a także zwykłego życia często uważany jest za powieść kompletną. <br>To imponujące arcydzieło dało podwaliny współczesnego powieściopisarstwa. Z „Wojny i pokoju" inspiracje czerpał Ernest Hemingway. Jej widowiskowy potencjał przez lata wykorzystywali artyści filmowi. Doczekała się wielu ekranizacji, a w 2016 roku BBC na jej podstawie stworzyło bardzo dobrze przyjęty przez widzów miniserial. Lew Tołstoj (1828-1910) - wybitny rosyjski pisarz i myśliciel. Jego utwory wywarły ogromny wpływ na literaturę światową, a poglądy na rozwój myśli etycznej pierwszej połowy XX wieku. Debiutował powieścią "Dzieciństwo" (1852), która wraz z "Latami chłopięcymi" (1854) i "Młodością" (1857) tworzy trylogię autobiograficzną. W latach 60. stworzył jedno ze swoich największych arcydzieł - "Wojnę i pokój". Stanowi ona rozległą panoramę rosyjskiego społeczeństwa doby napoleońskiej. Drugim najbardziej znanym utworem Tołstoja jest "Anna Karenina". Oprócz powieści autor pisał wiele opowiadań, utwory dramatyczne, a także zajmował się publicystyką polityczną.', 'bDG4DwAAQBAJ', 2077, 'http://books.google.com/books/publisher/content?id=bDG4DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE70zNFnF_086esdAjPO0hUlV5Hr0pyETCyR9bJgRE1qcvObY8IKtA2rVnGTeDd2tw3_HFToSE3q1iroASEBQMjT3VrQrUpzjLVbJxcikcLQod-rAaDfFsgaYluCazB8k5fs_5lTo&source=gbs_api', 0.00, 'Wojna i Pokój', 2018);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (21, 'Lew Tołstoj', 'Siedemnastoletnia Masza rozpacza po śmierci matki. Piękna i młoda Rosjanka miała w planach wyjazd za granicę, jednak rodzinna tragedia zaprzepaściła tę szansę. Dziewczyna została na świecie sama z młodszą siostrą Sonią oraz ich nauczycielką Katią. Niepewne swojego losu kobiety żyją w smutku i obawach o przyszłość. Ich losy zmienią się, gdy z wizytą do domu przybędzie Sergiusz Michałowicz, opiekun ich majątku. Dla Maszy będzie to początek nowego życia. Lew Tołstoj (1828-1910) - wybitny rosyjski pisarz i myśliciel. Jego utwory wywarły ogromny wpływ na literaturę światową, a poglądy na rozwój myśli etycznej pierwszej połowy XX wieku. Debiutował powieścią "Dzieciństwo" (1852), która wraz z "Latami chłopięcymi" (1854) i "Młodością" (1857) tworzy trylogię autobiograficzną. W latach 60. stworzył jedno ze swoich największych arcydzieł - "Wojnę i pokój". Stanowi ona rozległą panoramę rosyjskiego społeczeństwa doby napoleońskiej. Drugim najbardziej znanym utworem Tołstoja jest "Anna Karenina". Oprócz powieści autor pisał wiele opowiadań, utwory dramatyczne, a także zajmował się publicystyką polityczną.', 'Jja9DwAAQBAJ', 94, 'http://books.google.com/books/publisher/content?id=Jja9DwAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE70Sp1VGtdplSF-suDOTpSafR7Pi5aHJRW-5n1MDwPYJYuu0yYGNFJaxlg3nDL9xNmJ9W4mkvF8CjlElS1jbFgWD9SyHloj7WcDEGkOfYgwPEmNNCxhpq7ZRTsFyGREHroGw2Slp&source=gbs_api', 0.00, 'Szczęście rodzinne', 2019);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (22, 'Michal Viewegh', null, 'hqjSAAAACAAJ', 214, 'http://books.google.com/books/content?id=hqjSAAAACAAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE70HyJJ0qlA3k9zEPmxFv7Od_95-3vrduxQYYJd6rwB64WsK6sgggXmgQG2PCygEyO4jUxAJpr6R9Nk-_ljLW-dViPTEysD9EROKEJodC4XUp8-OQybwByXCzrGEZX-OR9UOiwZR&source=gbs_api', 0.00, 'Cudowne lata pod psem', 2004);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (23, 'J. R. R. Tolkien', 'This is the story of how a Baggins had an adventure, and found himself doing and saying things altogether unexpected... <p> ‘A flawless masterpiece’<br>The Times </p> <p>Bilbo Baggins is a hobbit who enjoys a comfortable, unambitious life, rarely travelling further than the pantry of his hobbit-hole in Bag End. But his contentment is disturbed when the wizard, Gandalf, and a company of thirteen dwarves arrive on his doorstep one day, to whisk him away on a journey ‘there and back again’. They have a plot to raid the treasure hoard of Smaug the Magnificent, a large and very dangerous dragon...</p> <p>The prelude to THE LORD OF THE RINGS, THE HOBBIT has sold many millions of copies since its publication in 1937, establishing itself as one of the most beloved and influential books of the twentieth century.</p>', 'U799AY3yfqcC', 320, 'http://books.google.com/books/content?id=U799AY3yfqcC&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE711QuHi4fsSxZawppNWcaCC_DE9YLkT3SQ16ccMOvUnBncInLOYoNnVMj_boNkdOTdj92e-usmkh-mASsfDDtWJbcTMxGePHZ87v0am8h02IfQPoqhba56GI9oRSSUIvKTwgSIF&source=gbs_api', 4.50, 'The Hobbit', 2009);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (24, 'J.R.R. Tolkien', '<p>Every December an envelope bearing a stamp from the North Pole would arrive for J.R.R. Tolkien’s children. Inside would be a letter in a strange, spidery handwriting and a beautiful colored drawing or painting. The letters were from Father Christmas.</p><p>They told wonderful tales of life at the North Pole: how the reindeer got loose and scattered presents all over the place; how the accident-prone North Polar Bear climbed the North Pole and fell through the roof of Father Christmas’s house into the dining room; how he broke the Moon into four pieces and made the Man in it fall into the back garden; how there were wars with the troublesome horde of goblins who lived in the caves beneath the house, and many more.</p><p>No reader, young or old, can fail to be charmed by Tolkien’s inventiveness in this classic holiday treat.</p>', 'HbG0jd66h3EC', 160, 'http://books.google.com/books/content?id=HbG0jd66h3EC&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE709L_DQ3GW74_k3EA8tZ-PGTuaM4WVcZaZ7fDq3ftGzSRTOmTcsGsPZSBzngQ481ApMzHrhgKyRENBX_1cKKm0zMLDIFuFs_-GIztGkEYx_nHFuU6qkDyK320mQuZi7Y6FL25v9&source=gbs_api', 4.00, 'Letters From Father Christmas', 2012);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (25, 'Rebecca F. Kuang', 'Pamiętasz wspaniałe opowieści o angielskich gentlemanach i ich eleganckim życiu?Ta książka nie będzie opowieścią o nich, ale o ludziach, na których plecach, spracowanych rękach i krwi zbudowano Imperium Brytyjskie z całą jego zamożnością, elitarnością i dobrym wychowaniem. Robin, osierocony pół-Chińczyk, trafia do Anglii, gdzie ma tylko jedno zadanie: uczyć się. Zdobywać wiedzę, poznawać nowe języki a potem odpracować dług wobec Imperium w oksfordzkim instytucie Babel. Wszystko zmienia się w dniu, a właściwie w nocy, gdy Robin poznaje pewnego człowieka. Bliźniaczo podobnego fizycznie, ale żyjącego według całkowicie innych zasad.To spotkanie zrujnuje misternie budowany świat Robina. Obróci wniwecz jego poczucie sprawiedliwości, wypali złudne poczucie bezpieczeństwa i zmusi do zadania sobie pytania: komu należy się moja wierność? W tej historii nie będzie prostych odpowiedzi ani gotowych recept. Będzie za to gorzka opowieść o wyzysku, niesprawiedliwości i bezwzględnej walce o władzę. Będzie nie mniej gorzka opowieść o próbie przywrócenia sprawiedliwości.Myślisz, że wiesz, co jest słuszne i właściwe? Spróbuj zmierzyć się z tą opowieścią.', 'jBiyEAAAQBAJ', 763, 'http://books.google.com/books/publisher/content?id=jBiyEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE72Hscm2eo0cOClYEQhbs7yNpaiHEss8CK-4x3RCJnA6NeisBX9G_gSLL_KSvK3ptPQzuOyfJBUZTrfSzVsdqs91t5mJJegPH_vXnHvD2aa44cBq6uanMF3qYLVDU3g3wicGgeLP&source=gbs_api', 0.00, 'Babel', 2023);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (26, 'Jakub Małecki', 'Porywająca opowieść o pięknie i okrucieństwie polskiej prowincjiKończy się wojna. Jan Łabendowicz odmawia pomocy uciekającej Niemce i ta przeklina jego rodzinę. Wkrótce na świat przychodzi syn Jana: chłopiec o śnieżnobiałej skórze. Kilkadziesiąt kilometrów dalej, w starej szafie wybucha zapomniany granat, burząc życie Bronka Geldy i jego kilkuletniej córki.Na losy Geldów i Łabendowiczów wpływają kolejne dziejowe zawirowania i przepowiednie, a także osobiste słabości i obsesje. Drogi obu rodzin przecinają się w zaskakujący i niespodziewany sposób. Na dobre zespaja je uczucie dwojga odmieńców: introwertycznego albinosa i okaleczonej w płomieniach dziewczyny.Dygot to powieść uwielbiana przez czytelników i ciesząca się uznaniem krytyki. Jakub Małecki w sposób godny mistrzów przeplata grozę istnienia z nostalgią za światem, który bezpowrotnie przeminął.', 'SfUuEAAAQBAJ', 336, 'http://books.google.com/books/publisher/content?id=SfUuEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE729LPd3FxSpjOFSmt_vXYII1rrh-9v4aZkT7F1qi_BqwHunDU04RvTvK-v5akj9aA6CccLbL8a3keEcoqDWIWPgjdoGK4o2wKu3D5_eb_Q928bFfmrv0Dv4Wbk22wcN9BQ9mtma&source=gbs_api', 0.00, 'Dygot (Wydanie IV)', 2021);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (27, 'Jakub Małecki', 'Czasami prawda rozciąga się daleko za horyzont.Jałowe lata bez wschodów i zachodów słońca. Wspomnienia jak burza piaskowa, jak skrzypienie desek w starym domu. Smugi przeszłości płytko pod skórą. Gry komputerowe i bułgarski rap.Maniek i Zuza mieszkają po sąsiedzku na warszawskim Mokotowie. On, były saper, próbuje spisać wspomnienia z misji w Afganistanie, mając nadzieję wreszcie stamtąd wrócić. Ona, dziecko pokolenia Y, uczy się żyć na własny rachunek i mimowolnie zatraca się w rodzinnej tajemnicy. Choć z pozoru nic ich nie łączy, ciążą ku sobie, niezdolni i niechętni do pielęgnowania więzi z otoczeniem.Nowa książka Jakuba Małeckiego to zaskakująca opowieść o tęsknocie, przyjaźni i odwadze. O konfrontacji z zadrą – własną i cudzą. O próbach znalezienia właściwej wersji siebie.', 'o7w8EAAAQBAJ', 336, 'http://books.google.com/books/publisher/content?id=o7w8EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE729Dih6-_9biEb9_j02kexDSVhJuXsnnT7EDDtGbCP0u69mrH4VohYgJCyqdRcGIUeqJ6CgvPMGvsLm0lPekRQxS_xgHIc0rpA-i4yyvD1IHKQuXMXXATZDZdMari8p4i86DyGC&source=gbs_api', 0.00, 'Horyzont', 2021);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (28, 'Lucy Hawking', null, 'Q3faNwAACAAJ', 299, 'http://books.google.com/books/content?id=Q3faNwAACAAJ&printsec=frontcover&img=1&zoom=1&imgtk=AFLRE71DWmalq4wyETAlMaajPDzZu4K7yXc0e19sUZdjxO9dTBaYj49FdFSqm_X8SRWlOcatT6Vz1dgmVwDFr8GgSJ5BeSNo_P8gDgpGLH8M5CDYMwXFeH6mhkrzDtSf5uW7-wWz_Jan&source=gbs_api', 0.00, 'Jerzy i tajny klucz do wszechświata', 2008);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (29, 'Grzegorz Walczak', 'Gdyby akcję kultowego serialu Breaking Bad umieszczono w Polsce, wyglądałaby właśnie tak.Ryszard J. zwany Profesorem potrafił w ciągu dwóch dni w polowych warunkach wyprodukować amfetaminę o wartości czterech milionów funtów. Brytyjska policja uznała jego narkotyki za najczystsze, jakie kiedykolwiek skonfiskowała. Nad substancjami, nad którymi pracował, pochylali się najlepsi eksperci.Kielecki chemik, elokwentny, a przede wszystkim doskonale wykształcony, przez lata skutecznie wymykał się sprawiedliwości. Wykorzystując skandaliczne luki w systemach prawnych państw w całej Europie, wodził za nos biegłych, prokuratorów i sędziów.Grzegorz Walczak od lat śledzi i opisuje w mediach sprawę Profesora oraz związane z jego postacią wydarzenia: pożar mieszkania w kieleckim wieżowcu, w którym zginęły trzy osoby, tajemniczy wybuch i śmierć byłego współpracownika. Przeprowadził wiele rozmów, przeczytał setki tomów akt, spędził dziesiątki godzin na opracowywaniu zebranych informacji i materiałów. Dzięki tej książce szeroko i wnikliwie prezentuje sylwetkę jednego z najbardziej inteligentnych polskich przestępców związanych z narkobiznesem, który do wytwarzania narkotyków nigdy się jednak nie przyznał.', 'h6y0EAAAQBAJ', 432, 'http://books.google.com/books/publisher/content?id=h6y0EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE71qxOdEfUeDmrzKgeY5hDC7MZNUgYKr_pD-TX5VQF-3KDAUYLxwWJ29QZSEcYEtWDtDvyVrBeV-dW-T7Wk24CC3StQkQ1KTOR7fZ-yX8HzEmCenNdaRp-gvMR_xu7jWxG5XYRTQ&source=gbs_api', 0.00, 'Profesor. Jak genialny chemik z Kielc stał się bossem narkobiznesu', 2023);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (30, ' Sonia Fernández-Vidal', 'Ta książka to niezwykła opowieść… o fizyce kwantowej. Chwila, chwila, poczekajcie! Nie odkładajcie jej na półkę, naprawdę nie ma się czego obawiać. Po prostu przyjmijcie nasze zaproszenie na wyjątkowe śniadanie – i sprawdźcie, co się na nim wydarzy!Jeśli zdecydujecie się zagłębić w lekturze, odkryjecie cudowny, zagadkowy świat. Fizyka kwantowa to jedna z najpiękniejszych i najbardziej zdumiewających teorii w nauce. Zawarte w niej prawa w zestawieniu z codziennym doświadczeniem wydają się zupełnie szalone, a przez to sprzeczne z intuicją. Kiedy tylko zanurzymy się w świat kwantów, nasze wyobrażenia o rzeczywistości, nawet tej najbardziej powszedniej, zaczynają się chwiać.Sonia Fernández-Vidal, pisarka i doktorka fizyki kwantowej, oraz Francesc Miralles, pisarz i dziennikarz, zapraszają nas na niezwykłe, zaskakujące i przesympatyczne śniadanie, na które przybędą również Newton, Einstein, Heisenberg i inni słynni fizycy. Przy pysznych muffinkach i pączkach, kawie i soku pomarańczowym, odbędziemy fascynującą i pełną niesamowitych odkryć podróż aż do początków wszechświata. Dowiemy się, czym jest tak zwana boska cząstka, jak to możliwe, że jedna rzecz może być w dwóch miejscach jednocześnie, odwiedzimy przedziwne Kasyno Heisenberga, wejdziemy w stan superpozycji, spróbujemy przetunelować przez to i owo… i być może odsłonimy rąbek tajemnicy istnienia.', 'Zb67EAAAQBAJ', 320, 'http://books.google.com/books/publisher/content?id=Zb67EAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE72u5dowxCNgaHHbI2xtgVqhGXdoXbrWYxDS1_UTWfOvIXAw1RGzKT30gaOJLT4dLB3E5c2bPDuUrzBxTYAm_ZUXkhdLLbi5HVLpqrBwy42Fa7relUWSmbdM9QZwQaC25vmXunl8&source=gbs_api', 0.00, 'Śniadanie z cząstkami, czyli jak ugryźć fizykę kwantową', 2023);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (31, 'Camilla Grebe', 'Pierwszorzędny thriller psychologiczny poruszający problem dyskryminacji i przemocy wobec kobietPowieść laureatki Szklanego Klucza 2020 za najlepszą skandynawską powieść kryminalnąPełen napięcia i zwrotów akcji kryminał poruszający kwestę dyskryminacji kobiet wykonujących zawody przypisane niegdyś mężczyznom. Grebe pokazuje losy trzech policjantek, które stając w obronie ofiar, same się nimi stają…W lutową noc 1944 roku w sztokholmskiej dzielnicy Klara zostaje znaleziona kobieta ukrzyżowana na podłodze. Trzydzieści lat później na jednym z sennych przedmieść miasta policja natrafia na kobietę zamordowaną w taki sam bestialski sposób. skutkiPościg za zabójcami okazuje się brzemienny w skutkach dla policjantek zajmujących się tymi przypadkami – dla Malin w latach 70., dla Hanne w latach 80. ubiegłego stulecia oraz dla Malin działającej współcześnie.Powyższy opis pochodzi od wydawcy.', 'm3IqEAAAQBAJ', 368, 'http://books.google.com/books/publisher/content?id=m3IqEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE73sxWaaHCfMRruViU0KdFYQv_ZGeowGkjlg18mp9lJmdoTaV5VXaayt4UGTf-BOVwnF9SPDgTL0EPqineFFDk9005qg6YaxMRfwbsEEQAEUXvykbtDxefO5ZDPkIyPqMftg7aDh&source=gbs_api', 0.00, 'Łowca cieni', 2021);
INSERT INTO public.book (id, author, description, external_id, pages, picture_url, rating, title, year) VALUES (32, 'Joanna Flis', '<i>Lepszy pijący mąż niż żaden.</i><i>To ja jestem winna, że nadużywa alkoholu. Gdybym była lepszą żoną, przestałby pić.</i><i>Muszę wszystko znieść i wytrzymać, bo dzieci powinny mieć matkę.</i><i>Gdy mój syn mi obiecuje, że więcej nie będzie ćpać, wierzę mu i odpuszczam. Do kolejnego razu...</i><i>Kiedy rodzice pili, byłam odpowiedzialna za swoje młodsze rodzeństwo. To przecież naturalne, że o rodzinę trzeba dbać w każdej sytuacji.</i>To jedne z najczęstszych przekonań osób współuzależnionych. Jeśli choć jedno kojarzy ci się z własną sytuacją, koniecznie musisz przeczytać tę książkę.Bliskimi osób uzależnionych, którzy postanawiają odwiedzić terapeutę, kierują przede wszystkim rozpacz, złość i lęk. Najczęściej ludzie przychodzą po pomoc, radę i wsparcie w konfrontacji z chorobą, na którą nie mają wpływu. Tymczasem jedyne, co można zrobić, to zawalczyć o siebie.Niewiele jest narzędzi, które pozwolą realnie wpłynąć na uzależnienie bliskiej ci osoby. Jednak jest sporo takich, dzięki którym uzyskasz wpływ na własne życie. Autorka przedstawia ćwiczenia praktykowane podczas sesji terapeutycznych. Poznasz profesjonalne, przystępne informacje z konkretnymi poradami, które z łatwością będziesz mógł sam zastosować i w końcu zacząć żyć tak, jak chcesz.Joanna Flis, specjalista psychoterapii uzależnień, zapewnia bezpieczną przestrzeń, w której zajmiesz się przede wszystkim sobą i wszystkimi aspektami swojej relacji z osobą uzależnioną. To jedyny sposób, aby przerwać ciąg nieudanych prób i wygrać walkę, którą toczysz być może od lat. ', 'KV5xEAAAQBAJ', 200, 'http://books.google.com/books/publisher/content?id=KV5xEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&imgtk=AFLRE70dKQkVJpYIXR1a18PVK3-NICaT9yf2VUyIcaEGXn0ZbGEJnTc0BZgxO6-I4sfZkpW17GJKKOaAhQzdgsIMdrSoCFZgzUeja3okD4gNL-Nbj_ZFtjTUpwfd6XE61Ximf08BmO36&source=gbs_api', 0.00, 'Współuzależnieni. Jak zatroszczyć się o siebie i budować zdrowe relacje z osobami uzależnionymi', 2022);
